/**
 * Parser de archivos Excel (.xlsx, .xls)
 * 
 * @author Antonio Rodriguez Smith - DXC Technology
 * @client Banco Sabadell - TDM (Manuel Gil)
 */

class ParserExcel {
    constructor() {
        this.libreria = XLSX; // SheetJS
    }

    /**
     * Parsear archivo Excel de casos
     * @param {File} archivo - Archivo Excel
     * @returns {Promise<Object>} Casos parseados
     */
    async parsearCasos(archivo) {
        try {
            const datos = await this.leerArchivo(archivo);
            const workbook = this.libreria.read(datos, { type: 'binary' });

            // Leer hoja de casos
            const hojaCasos = workbook.Sheets['Casos'];
            const casos = this.libreria.utils.sheet_to_json(hojaCasos);

            // Leer hoja de acciones
            const hojaAcciones = workbook.Sheets['Acciones'];
            const acciones = this.libreria.utils.sheet_to_json(hojaAcciones);

            // Combinar casos con sus acciones
            return this.combinarCasosAcciones(casos, acciones);

        } catch (error) {
            console.error('Error parseando Excel de casos:', error);
            throw new Error(`No se pudo parsear el archivo: ${error.message}`);
        }
    }

    /**
     * Parsear archivo Excel de condiciones
     * @param {File} archivo - Archivo Excel
     * @returns {Promise<Array>} Condiciones parseadas
     */
    async parsearCondiciones(archivo) {
        try {
            const datos = await this.leerArchivo(archivo);
            const workbook = this.libreria.read(datos, { type: 'binary' });

            const hojaCondiciones = workbook.Sheets['Condiciones'];
            const condiciones = this.libreria.utils.sheet_to_json(hojaCondiciones);

            // Validar estructura
            this.validarEstructuraCondiciones(condiciones);

            return condiciones.filter(c => c.Activo === 'SI');

        } catch (error) {
            console.error('Error parseando Excel de condiciones:', error);
            throw new Error(`No se pudo parsear el archivo: ${error.message}`);
        }
    }

    /**
     * Leer archivo como binario
     */
    leerArchivo(archivo) {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();

            reader.onload = (e) => {
                resolve(e.target.result);
            };

            reader.onerror = (e) => {
                reject(new Error('Error leyendo archivo'));
            };

            reader.readAsBinaryString(archivo);
        });
    }

    /**
     * Combinar casos con sus acciones
     */
    combinarCasosAcciones(casos, acciones) {
        return casos.map(caso => {
            const accionesCaso = acciones.filter(a => a['ID Caso'] === caso.ID);

            return {
                id: caso.ID,
                nombre: caso['Nombre Caso'],
                descripcion: caso.Descripci√≥n,
                metadata: {
                    tipoCliente: caso['Tipo Cliente'],
                    tycFirmado: caso['TyC Firmado'],
                    tcComplejo: caso['TC Complejo'],
                    tcNoComplejo: caso['TC No Complejo'],
                    tipoMifid: caso['Tipo MIFID'],
                    titularidad: caso.Titularidad,
                    configurable: caso.Configurable,
                    arquetipo: caso.Arquetipo
                },
                acciones: accionesCaso.map(a => ({
                    numero: a['Num Acci√≥n'],
                    descripcion: a['Descripci√≥n Acci√≥n'],
                    palabrasClave: a['Palabras Clave']?.split(',').map(p => p.trim()) || [],
                    dominioEsperado: a['Dominio Esperado']
                }))
            };
        });
    }

    /**
     * Validar estructura de condiciones
     */
    validarEstructuraCondiciones(condiciones) {
        const camposRequeridos = ['Dominio', 'C√≥digo Condici√≥n', 'Nombre', 'Descripci√≥n'];

        condiciones.forEach((cond, idx) => {
            camposRequeridos.forEach(campo => {
                if (!cond[campo]) {
                    throw new Error(
                        `Fila ${idx + 2}: El campo "${campo}" es obligatorio`
                    );
                }
            });
        });
    }

    /**
     * Detectar si el archivo es v√°lido
     */
    static esArchivoExcel(archivo) {
        const extensionesValidas = ['.xlsx', '.xls'];
        return extensionesValidas.some(ext => 
            archivo.name.toLowerCase().endsWith(ext)
        );
    }
}

// Exportar
window.ParserExcel = ParserExcel;
```

