```javascript
/**
 * Validador de archivos con seguridad reforzada
 * @author Conejo - DXC Technology
 */
class ValidadorSeguro {

    /**
     * Validar archivo antes de procesarlo
     */
    static validarArchivo(archivo) {
        const errores = [];

        // 1. Validar nombre de archivo (evitar path traversal)
        if (this.contieneCaracteresInseguros(archivo.name)) {
            errores.push('Nombre de archivo contiene caracteres no permitidos');
        }

        // 2. Validar extensi√≥n (whitelist)
        if (!this.esExtensionPermitida(archivo.name)) {
            errores.push('Formato de archivo no permitido');
        }

        // 3. Validar tama√±o
        if (archivo.size > SECURITY_CONFIG.maxTamanoArchivo) {
            errores.push(`Archivo excede tama√±o m√°ximo (${SECURITY_CONFIG.maxTamanoArchivo / 1024 / 1024}MB)`);
        }

        // 4. Validar MIME type
        if (!this.esMimeTypeValido(archivo.type)) {
            errores.push('Tipo MIME no v√°lido');
        }

        if (errores.length > 0) {
            throw new SecurityError(errores.join('. '));
        }

        return true;
    }

    /**
     * Detectar caracteres peligrosos
     */
    static contieneCaracteresInseguros(nombre) {
        const patronesPeligrosos = [
            /\.\./,           // Path traversal
            /[<>:"|?*]/,      // Caracteres prohibidos en Windows
            /[\x00-\x1f]/,    // Caracteres de control
            /^\.+$/,          // Solo puntos
            /\s{2,}/          // M√∫ltiples espacios
        ];

        return patronesPeligrosos.some(patron => patron.test(nombre));
    }

    /**
     * Validar extensi√≥n contra whitelist
     */
    static esExtensionPermitida(nombre) {
        const extension = nombre.toLowerCase().substring(nombre.lastIndexOf('.'));
        return SECURITY_CONFIG.formatosPermitidos.includes(extension);
    }

    /**
     * Validar MIME type
     */
    static esMimeTypeValido(mimeType) {
        const mimeTypesPermitidos = [
            'text/markdown',
            'text/plain',
            'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
            'application/vnd.ms-excel',
            'text/csv',
            'application/json'
        ];

        return mimeTypesPermitidos.includes(mimeType) || mimeType === '';
    }
}

class SecurityError extends Error {
    constructor(message) {
        super(message);
        this.name = 'SecurityError';
    }
}
