/**
 * Utilidades de sanitizaci√≥n
 * @author Conejo - DXC Technology
 */
class Sanitizador {

    /**
     * Escapar HTML para prevenir XSS
     */
    static escaparHTML(texto) {
        const mapa = {
            '&': '&amp;',
            '<': '&lt;',
            '>': '&gt;',
            '"': '&quot;',
            "'": '&#x27;',
            '/': '&#x2F;'
        };

        return String(texto).replace(/[&<>"'/]/g, (c) => mapa[c]);
    }

    /**
     * Limpiar entrada de usuario
     */
    static limpiarEntrada(entrada) {
        if (typeof entrada !== 'string') return '';

        // Eliminar caracteres de control
        let limpia = entrada.replace(/[\x00-\x1F\x7F]/g, '');

        // Limitar longitud
        limpia = limpia.substring(0, 10000);

        // Trim
        limpia = limpia.trim();

        return limpia;
    }

    /**
     * Validar y sanitizar JSON
     */
    static sanitizarJSON(jsonString) {
        try {
            const parsed = JSON.parse(jsonString);

            // Verificar que no exceda profundidad
            if (this.obtenerProfundidadObjeto(parsed) > 10) {
                throw new SecurityError('JSON con profundidad excesiva');
            }

            return parsed;

        } catch (e) {
            throw new SecurityError('JSON no v√°lido o inseguro');
        }
    }

    /**
     * Calcular profundidad de objeto
     */
    static obtenerProfundidadObjeto(obj, profundidad = 0) {
        if (profundidad > 10) return profundidad;
        if (typeof obj !== 'object' || obj === null) return profundidad;

        const profundidades = Object.values(obj).map(valor => 
            this.obtenerProfundidadObjeto(valor, profundidad + 1)
        );

        return Math.max(profundidad, ...profundidades);
    }
}
