/**
 * Logger de auditor√≠a
 * @author Conejo - DXC Technology
 */
class AuditoriaLogger {

    constructor() {
        this.logs = [];
        this.maxLogs = 1000;
    }

    /**
     * Registrar acci√≥n
     */
    registrar(evento, detalles = {}) {
        const entrada = {
            timestamp: new Date().toISOString(),
            evento: evento,
            usuario: 'Conejo',
            cliente: 'Banco Sabadell - TDM',
            detalles: detalles,
            sessionId: this.obtenerSessionId()
        };

        this.logs.push(entrada);

        // Limitar tama√±o del array
        if (this.logs.length > this.maxLogs) {
            this.logs.shift();
        }

        // Log en consola (solo en desarrollo)
        if (this.esEntornoDesarrollo()) {
            console.log(`[AUDITOR√çA] ${evento}`, detalles);
        }

        // En producci√≥n, enviar a servidor de logs
        if (this.esEntornoProduccion()) {
            this.enviarAServidor(entrada);
        }
    }

    /**
     * Obtener logs de auditor√≠a
     */
    obtenerLogs(filtros = {}) {
        let resultado = [...this.logs];

        if (filtros.evento) {
            resultado = resultado.filter(log => log.evento === filtros.evento);
        }

        if (filtros.desde) {
            resultado = resultado.filter(log => new Date(log.timestamp) >= new Date(filtros.desde));
        }

        return resultado;
    }

    /**
     * Exportar logs para auditor√≠a
     */
    exportarLogs() {
        const blob = new Blob(
            [JSON.stringify(this.logs, null, 2)], 
            { type: 'application/json' }
        );

        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `auditoria_${new Date().toISOString().split('T')[0]}.json`;
        a.click();
        URL.revokeObjectURL(url);
    }

    obtenerSessionId() {
        if (!sessionStorage.getItem('iip_session_id')) {
            sessionStorage.setItem('iip_session_id', this.generarId());
        }
        return sessionStorage.getItem('iip_session_id');
    }

    generarId() {
        return 'sess_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
    }

    esEntornoDesarrollo() {
        return window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1';
    }

    esEntornoProduccion() {
        return !this.esEntornoDesarrollo();
    }

    enviarAServidor(entrada) {
        // Placeholder para integraci√≥n futura con servidor de logs
        // fetch('/api/logs', { method: 'POST', body: JSON.stringify(entrada) });
    }
}

