// Validaci√≥n de dominios permitidos
validarDestinatario(email) {
  const dominiosPermitidos = CONFIG_EMAIL.configuracion.emailsPermitidos;

  const dominio = '@' + email.split('@')[1];

  if (!dominiosPermitidos.some(d => dominio.endsWith(d))) {
    throw new Error(
      `Solo se permiten emails de: ${dominiosPermitidos.join(', ')}`
    );
  }
}

// Rate limiting
verificarLimiteDiario() {
  const hoy = new Date().toDateString();
  const contador = localStorage.getItem(`email_count_${hoy}`) || 0;

  if (contador >= CONFIG_EMAIL.configuracion.limiteDiario) {
    throw new Error('Se ha alcanzado el l√≠mite diario de emails');
  }

  localStorage.setItem(`email_count_${hoy}`, parseInt(contador) + 1);
}
