  construirMailto(opciones) {
    const params = new URLSearchParams();

    // Destinatarios
    const destinatarios = Array.isArray(opciones.destinatario)
      ? opciones.destinatario.join(';')
      : opciones.destinatario;

    // CC (opcional)
