  /**
   * Validar opciones
   */
  validarOpciones(opciones) {
    if (!opciones.destinatario) {
      throw new Error('El destinatario es obligatorio');
    }

    if (!opciones.asunto) {
      throw new Error('El asunto es obligatorio');
    }

    // Validar email
    const emails = Array.isArray(opciones.destinatario)
      ? opciones.destinatario
      : [opciones.destinatario];

    emails.forEach(email => {
      if (!this.validarEmail(email)) {
        throw new Error(`Email no v√°lido: ${email}`);
      }
    });
  }

  /**
   * Validar formato de email
   */
  validarEmail(email) {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
  }

  /**
   * Mostrar notificaci√≥n
   */
  mostrarNotificacion(mensaje, tipo) {
    if (window.mostrarNotificacion) {
      window.mostrarNotificacion(mensaje, tipo);
