      destinatario: destinatarios,
      asunto: `[IIP DXC] An√°lisis de Condiciones - ${new Date().toLocaleDateString('es-ES')}`,
      resultados: resultados,
      mensaje: 'Adjunto encontrar√° el an√°lisis completo de condiciones ICARIA.',
      nombreDestinatario: this.extraerNombre(
        Array.isArray(destinatarios) ? destinatarios[0] : destinatarios
      )
    };

    return this.enviarPorOutlook(opciones);
  }

  /**
   * Enviar validaci√≥n individual
   */
  enviarValidacion(texto, condiciones, destinatarios) {
    let mensaje = `Se ha validado el siguiente texto:\n\n"${texto}"\n\n`;
    mensaje += `Se encontraron ${condiciones.length} condiciones relacionadas:\n\n`;

    condiciones.slice(0, 5).forEach((c, idx) => {
      mensaje += `${idx + 1}. ${c.nombre} (${c.scoring}% similitud)\n`;
      mensaje += `   Dominio: ${c.dominio}\n`;
      mensaje += `   ${c.descripcion}\n\n`;
    });

    const opciones = {
      destinatario: destinatarios,
      asunto: '[IIP DXC] Validaci√≥n de Texto Individual',
      mensaje: mensaje,
      nombreDestinatario: this.extraerNombre(
        Array.isArray(destinatarios) ? destinatarios[0] : destinatarios
      )
    };

    return this.enviarPorOutlook(opciones);
  }

  /**
   * Extraer nombre del email
   */
  extraerNombre(email) {
    const nombre = email.split('@')[0].replace(/[._-]/g, ' ');
    return nombre
      .split(' ')
      .map(p => p.charAt(0).toUpperCase() + p.slice(1).toLowerCase())
      .join(' ');
  }
}

// Exportar
window.GestorEmailOutlook = GestorEmailOutlook;
