**Para entorno corporativo Banco Sabadell con autenticaci√≥n SSO**

```javascript
/**
 * Gestor de Email con Microsoft Graph API
 * Para entorno corporativo Banco Sabadell
 */

class GestorEmailGraphAPI {
  constructor() {
    this.clientId = CONFIG_EMAIL.outlook.clientId;
    this.tenantId = CONFIG_EMAIL.outlook.tenantId;
    this.redirectUri = window.location.origin;
    this.scopes = ['Mail.Send', 'User.Read'];
    this.accessToken = null;
  }

  /**
   * Autenticar con Microsoft
   */
  async autenticar() {
    try {
      // Usar MSAL (Microsoft Authentication Library)
      const msalConfig = {
        auth: {
          clientId: this.clientId,
          authority: `https://login.microsoftonline.com/${this.tenantId}`,
          redirectUri: this.redirectUri
        }
      };

      const msalInstance = new msal.PublicClientApplication(msalConfig);

      const loginRequest = {
        scopes: this.scopes
      };

      const response = await msalInstance.loginPopup(loginRequest);
      this.accessToken = response.accessToken;

      auditoria.registrar('EMAIL_AUTENTICACION_EXITOSA', {
        usuario: response.account.username
      });

      return true;

    } catch (error) {
      console.error('[EMAIL] Error autenticaci√≥n:', error);
      auditoria.registrar('EMAIL_AUTENTICACION_ERROR', { error: error.message });
      return false;
    }
  }

  /**
   * Enviar email usando Graph API
   */
  async enviarEmail(opciones) {
    try {
      // Autenticar si es necesario
      if (!this.accessToken) {
        const autenticado = await this.autenticar();
        if (!autenticado) {
          throw new Error('No se pudo autenticar con Microsoft');
        }
      }

      // Construir mensaje
      const mensaje = this.construirMensajeGraph(opciones);

      // Enviar via Graph API
      const response = await fetch('https://graph.microsoft.com/v1.0/me/sendMail', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.accessToken}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(mensaje)
      });

      if (!response.ok) {
        throw new Error(`Error HTTP: ${response.status}`);
      }

      auditoria.registrar('EMAIL_ENVIADO_GRAPH', {
        destinatarios: opciones.destinatario
      });

      this.mostrarNotificacion('‚úÖ Email enviado correctamente', 'success');
      return true;

    } catch (error) {
      console.error('[EMAIL] Error al enviar:', error);
      this.mostrarNotificacion(`‚ùå Error: ${error.message}`, 'error');
      return false;
    }
  }

  /**
   * Construir mensaje para Graph API
   */
  construirMensajeGraph(opciones) {
    // Preparar destinatarios
    const toRecipients = (Array.isArray(opciones.destinatario)
      ? opciones.destinatario
      : [opciones.destinatario]
    ).map(email => ({
      emailAddress: { address: email }
    }));

    // Preparar CC
    const ccRecipients = opciones.cc
      ? (Array.isArray(opciones.cc) ? opciones.cc : [opciones.cc]).map(email => ({
          emailAddress: { address: email }
        }))
      : [];

    // Construir cuerpo HTML
    const cuerpoHTML = this.generarCuerpoHTML(opciones);

    // Mensaje completo
    return {
      message: {
        subject: opciones.asunto,
        body: {
          contentType: 'HTML',
          content: cuerpoHTML
        },
        toRecipients: toRecipients,
        ccRecipients: ccRecipients,
        importance: opciones.importancia || 'normal',
        from: {
          emailAddress: {
            name: 'IIP DXC - ICARIA Platform',
            address: 'no-reply@dxc.com'
